	/**
	 * 生成16位随机数字
	 * @param count
	 * @return
	 */
	public static String getRandomNumString(int count){
		String[] cs={"1","2","3","4","5","6","7","8","9","0"};
		StringBuffer sb=new StringBuffer();
		for(int i=0;i<count;i++){
			sb.append(cs[new Random().nextInt(10)]);
		}
		return sb.toString();
	}
